import './index.css'
import {Component} from 'react'

class ClickCounter extends Component {
  state = {counter: 0}

  render() {
    const {counter} = this.state
    return (
      <div>
        <h1>
          The Button has been clicked <span>{counter}</span> times
        </h1>
      </div>
    )
  }
}
export default ClickCounter
