import './index.css'
import {Component} from 'react'

class EvenOddApp extends Component {
  state = {
    isEven: true,
    countNum: 0,
  }

  onIncrement = () => {
    const {countNum} = this.state
    let randomNum = Math.random()
    randomNum = Math.ceil(randomNum * 100)

    console.log(countNum + randomNum)
    let checkEvenOdd
    if ((countNum + randomNum) % 2 === 0) {
      checkEvenOdd = false
    } else {
      checkEvenOdd = true
    }

    return this.setState(prevState => ({
      isEven: checkEvenOdd,
      countNum: prevState.countNum + randomNum,
    }))
  }

  render() {
    const {isEven, countNum} = this.state

    const textEvenOdd = () => {
      if (isEven) {
        return <p>Count is Even</p>
      }
      return <p>Count is Odd</p>
    }

    return (
      <div>
        <h1>Count {countNum}</h1>
        {textEvenOdd()}
        <button type="button" onClick={this.onIncrement}>
          Increment
        </button>
        <p>*Increase By Random Number Between 0 and 100</p>
      </div>
    )
  }
}

export default EvenOddApp
