import './index.css'
import {Component} from 'react'

class EvenOddApp extends Component {
  state = {
    isEven: true,
    countNum: 0,
  }

  onIncrement = () => {
    let randomNum = Math.random()
    randomNum = Math.ceil(randomNum * 100)
    let checkEvenOdd

    this.setState(prevState => {
      if (randomNum % 2 === 0) {
        checkEvenOdd = true
      } else {
        checkEvenOdd = false
      }
      return {isEven: checkEvenOdd, countNum: prevState + randomNum}
    })
  }

  render() {
    const {isEven, countNum} = this.state

    let textEvenOdd
    if (isEven) {
      textEvenOdd = <p>Count is Even</p>
    } else {
      textEvenOdd = <p>Count is Odd</p>
    }

    return (
      <div>
        <h1>Count {countNum}</h1>
        {textEvenOdd}
        <button type="button" onClick={this.onIncrement}>
          Increment
        </button>
        <p>*Increase By Random Number Between 0 and 100</p>
      </div>
    )
  }
}

export default EvenOddApp
