import './index.css'
import {Component} from 'react'

class ShowHide extends Component {
  state = {
    isFirstNameHidden: true,
    isLastNameHidden: true,
  }

  showHideFirstName = () =>
    this.setState(prevState => ({
      isFirstNameHidden: !prevState.isFirstNameHidden,
    }))

  render() {
    const {isFirstNameHidden, isLastNameHidden} = this.state

    const showFirstName = () => {
      if (!isFirstNameHidden) {
        return (
          <div>
            <button type="button" onClick={this.showHideFirstName}>
              Show/Hide Firstname
            </button>
            <div>
              <p>Joe</p>
            </div>
          </div>
        )
      }
      return (
        <div>
          <button type="button">Show/Hide Firstname</button>
        </div>
      )
    }

    return (
      <div>
        <h1>Show/Hide</h1>
        <div>
          {showFirstName()}
          <div>
            <button type="button">Show/Hide Lastname</button>
            <div>
              <p>Jonas</p>
            </div>
          </div>
        </div>
      </div>
    )
  }
}

export default ShowHide
