import './index.css'
import {Component} from 'react'

class RandomNumberGenerator extends Component {
  state = {randomNumber: 0}

  generateNumber = () => {
    let newRandomNumber = Math.random() * 100
    newRandomNumber = Math.ceil(newRandomNumber)
    this.setState({randomNumber: newRandomNumber})
  }

  render() {
    const {randomNumber} = this.state

    return (
      <div className="bg-container">
        <div className="card-container">
          <h1 className="heading-text">Random Number</h1>
          <p className="description">
            Generate a random number in the range 0 to 100
          </p>
          <button
            className="button"
            type="button"
            onClick={this.generateNumber}
          >
            Generate
          </button>
          <h1 className="random-number-text">{randomNumber}</h1>
        </div>
      </div>
    )
  }
}

export default RandomNumberGenerator
