import './index.css'
import {Component} from 'react'

class LettersCalculator extends Component {
  state = {searchInput: ''}

  countSearchInput = event => {
    const inputValue = event.target.value
    const inputValueArray = inputValue.split('')
    const nonEmptyValue = inputValueArray.filter(eachChar => eachChar !== ' ')
    nonEmptyValue.join('')
    this.setState({searchInput: inputValue})
  }

  render() {
    const {searchInput} = this.state

    console.log(searchInput)

    return (
      <div className="bg-container">
        <img
          className="image"
          src="https://assets.ccbp.in/frontend/react-js/stop-watch-with-calculator-img.png"
          alt="letters calculator"
        />
        <div className="text-container">
          <h1 className="heading-text">Calculate the Letters you enter</h1>
          <p className="search-description">Enter the phrase</p>
          <input
            className="search-input"
            type="search"
            placeholder="Enter the Phrase"
            onChange={this.countSearchInput}
          />
          <div className="letter-count-container">
            <p className="letters-count-text">No. of letters: 10</p>
          </div>
        </div>
      </div>
    )
  }
}

export default LettersCalculator
