  },
  {
    id: 4,
    timeAccessed: '04:00 PM',
    logoUrl: 'https://assets.ccbp.in/frontend/react-js/hashnode-img.png',
    title: 'Hashnode: Everything you need to start blogging as a developer!',
    domainUrl: 'hashnode.com',
  },
  {
    id: 5,
    timeAccessed: '03:25 PM',
    logoUrl: 'https://assets.ccbp.in/frontend/react-js/github-img.png',
    title: 'GitHub: Where the world builds software · GitHub',
    domainUrl: 'github.com',
  },

  {
    id: 6,
    timeAccessed: '02:45 PM',
    logoUrl: 'https://assets.ccbp.in/frontend/react-js/react-img.png',
    title: 'React – A JavaScript library for building user interfaces',
    domainUrl: 'reactjs.org',
  },
  {
    id: 7,
    timeAccessed: '01:25 PM',
    logoUrl: 'https://assets.ccbp.in/frontend/react-js/stackoverflow-img.png',
    title: 'Stack Overflow - Where Developers Learn, Share, & Build Careers',
    domainUrl: 'stackoverflow.com',
  },

  {
    id: 8,
    timeAccessed: '09:25 AM',
    logoUrl: 'https://assets.ccbp.in/frontend/react-js/gmail-img.png',
    title: 'Gmail',
    domainUrl: 'mail.google.com',
  },
  {
    id: 9,
    timeAccessed: '09:00 AM',
    logoUrl: 'https://assets.ccbp.in/frontend/react-js/google-img.png',
    title: 'Google',
    domainUrl: 'google.com',
  },
]

// Replace your code here
class App extends Component {
  state = {searchInput: '', historyList: initialHistoryList}

  searchHistory = event => {
    this.setState({searchInput: event.target.value})
  }

  onDeleteUpdateHistory = historyId => {
    const {historyList} = this.state
    const updatedHistoryList = historyList.filter(
      eachHistory => eachHistory.id !== historyId,
    )
    this.setState({historyList: updatedHistoryList})
  }

  getSearchHistoryList = () => {
    const {searchInput, historyList} = this.state
    const searchHistoryList = historyList.filter(eachHistory => {
      const eachSearch = eachHistory.title.toLowerCase()
      return eachSearch.includes(searchInput.toLowerCase())
    })
    return searchHistoryList
  }

  render() {
    const searchHistoryList = this.getSearchHistoryList()

    return (
      <div>
        <Header searchHistory={this.searchHistory} />
        <ul className="history-items-container">
          {searchHistoryList.map(eachHistory => (
            <HistoryItem
              historyDetail={eachHistory}
              onDeleteHistory={this.onDeleteUpdateHistory}
              key={eachHistory.id}
            />
          ))}
        </ul>
      </div>
    )
  }
}

export default App
