import {Component} from 'react'

const countryAndCapitalsList = [
  {
    id: 'NEW_DELHI',
    capitalDisplayText: 'New Delhi',
    country: 'India',
  },
  {
    id: 'LONDON',
    capitalDisplayText: 'London',
    country: 'United Kingdom',
  },
  {
    id: 'PARIS',
    capitalDisplayText: 'Paris',
    country: 'France',
  },
  {
    id: 'KATHMANDU',
    capitalDisplayText: 'Kathmandu',
    country: 'Nepal',
  },
  {
    id: 'HELSINKI',
    capitalDisplayText: 'Helsinki',
    country: 'Finland',
  },
]

class Capitals extends Component {
  state = {activeCountry: countryAndCapitalsList[0].country}

  findCountry = event => {
    const countryValue = event.target.value
    console.log(countryValue)
  }

  render() {
    const {activeCountry} = this.state

    return (
      <div>
        <div>
          <h1>Countries and Capitals</h1>
          <div>
            <select name="capitals" onChange={this.findCountry}>
              {countryAndCapitalsList.map(eachObj => (
                <option value={eachObj.country}>
                  {eachObj.capitalDisplayText}
                </option>
              ))}
            </select>
            <p>is the capital of which country?</p>
          </div>
          <p>{activeCountry}</p>
        </div>
      </div>
    )
  }
}

export default Capitals
