import './index.css'

const TabItem = props => {
  const {tabDetails, activeTabId} = props

  let activeTabClass
  if (tabDetails.id === activeTabId) {
    activeTabClass = 'active-tab'
  } else {
    activeTabClass = ''
  }

  return (
    <li className="tab-item">
      <button className="tab-button" type="button">
        {tabDetails.displayText}
      </button>
    </li>
  )
}

export default TabItem
