import './index.css'

const TabItem = props => {
  const {tabDetails, activeTabId} = props

  const activeTabClass = tabDetails.id === activeTabId ? 'active-tab' : ''

  return (
    <li className="tab-item">
      <button className={`tab-button ${activeTabClass}`} type="button">
        {tabDetails.displayText}
      </button>
    </li>
  )
}

export default TabItem
