import './index.css'
import {Component} from 'react'

class CoinToss extends Component {
  state = {tossedCoin: 0, headsCount: 0, tailsCount: 0, totalCount: 0}

  tossCoin = () => {
    let headCount
    let tailCount

    const tossResult = Math.floor(Math.random() * 2)
    if (tossResult === 0) {
      headCount = 1
      tailCount = 0
    } else {
      tailCount = 1
      headCount = 0
    }

    this.setState({tossedCoin: tossResult})
  }

  render() {
    const {tossedCoin, headsCount, tailsCount, totalCount} = this.state

    let imgUrl
    if (tossedCoin === 0) {
      imgUrl = 'https://assets.ccbp.in/frontend/react-js/heads-img.png'
    } else {
      imgUrl = 'https://assets.ccbp.in/frontend/react-js/tails-img.png'
    }

    return (
      <div>
        <div>
          <h1>Coin Toss Game</h1>
          <p>Heads (or) Tails</p>
          <img src={imgUrl} alt="toss result" />
          <button type="button" onClick={this.tossCoin}>
            Toss Coin
          </button>
          <div>
            <p>Total:{totalCount}</p>
            <p>Heads:{headsCount}</p>
            <p>Tails:{tailsCount}</p>
          </div>
        </div>
      </div>
    )
  }
}

export default CoinToss
