import './index.css'
import {Component} from 'react'
import {v4 as uuidv4} from 'uuid'
import MoneyDetails from '../MoneyDetails'
import TransactionItem from '../TransactionItem'

const transactionTypeOptions = [
  {
    optionId: 'INCOME',
    displayText: 'Income',
  },
  {
    optionId: 'EXPENSES',
    displayText: 'Expenses',
  },
]

// Money Manager Main

class MoneyManager extends Component {
  state = {
    title: '',
    amount: '',
    transType: 'Income',
    transDetail: {
      balance: 0,
      income: 0,
      expense: 0,
    },
    historyList: [],
  }

  updateTitle = event => this.setState({title: event.target.value})

  updateAmount = event => this.setState({amount: event.target.value})

  updateTransType = event => this.setState({transType: event.target.value})

  addTransaction = () => {
    const {title, amount, transType, transDetail} = this.state
    const {balance, income, expense} = transDetail
    let updatedBalance
    let updatedIncome
    let updatedExpense

    if (transType === 'Income') {
      updatedBalance = balance + parseInt(amount)
      updatedIncome = income + parseInt(amount)
      updatedExpense = expense
    } else {
      updatedBalance = balance - parseInt(amount)
      updatedExpense = expense + parseInt(amount)
      updatedIncome = income
    }

    const newTransactionObj = {
      id: uuidv4(),
      title,
      amount,
      transType,
    }

    this.setState(prevState => {
      const {historyList} = prevState
      const updatedHistoryList = [...historyList, newTransactionObj]
      return {
        title: '',
        amount: '',
        transType: 'Income',
        transDetail: {
          balance: updatedBalance,
          income: updatedIncome,
          expense: updatedExpense,
        },
        historyList: updatedHistoryList,
      }
    })
  }

  deleteHistory = historyId => {
    const {historyList, transDetail} = this.state
    const {balance, income, expense} = transDetail
    let updatedTransDetails

    const updateTranseDetail = objToDelete => {
      let updatedBalance = balance
      let updatedIncome = income
      let updatedExpense = expense

      if (objToDelete.transType === 'Income') {
        updatedBalance = balance - parseInt(objToDelete.amount)
        updatedIncome = income - parseInt(objToDelete.amount)
      } else {
        updatedBalance = balance + parseInt(objToDelete.amount)
        updatedExpense = expense - parseInt(objToDelete.amount)
      }
      updatedTransDetails = {
        balance: updatedBalance,
        income: updatedIncome,
        expense: updatedExpense,
      }
    }

    const updatedHistoryList = historyList.filter(eachObj => {
      if (eachObj.id === historyId) {
        updateTranseDetail(eachObj)
      }
      return eachObj.id !== historyId
    })
    this.setState({
      transDetail: updatedTransDetails,
      historyList: updatedHistoryList,
    })
  }

  render() {
    const {title, amount, transType, historyList, transDetail} = this.state

    return (
      <div className="main-bg-container">
        <div className="name-container">
          <h1 className="name-text">Hi, Richard</h1>
          <p className="description">
            Welcome back to your <span>Money Manager</span>
          </p>
        </div>

        <MoneyDetails transDetail={transDetail} />

        <div className="add-trans-history-container">
          <div className="add-transaction-container">
            <h1 className="add-transaction-heading">Add Transaction</h1>
            <form className="add-transaction-form">
              <div className="title-container">
                <label className="title" htmlFor="title">
                  TITLE
                </label>
                <input
                  className="title-input"
                  id="title"
                  type="text"
                  placeholder="TITLE"
                  value={title}
                  onChange={this.updateTitle}
                />
              </div>
              <div className="amount-container">
                <label htmlFor="amount" className="amount">
                  AMOUNT
                </label>
                <input
                  className="amount-input"
                  id="amount"
                  type="text"
                  placeholder="AMOUNT"
                  value={amount}
                  onChange={this.updateAmount}
                />
              </div>
              <select
                className="transaction-type"
                value={transType}
                onChange={this.updateTransType}
              >
                {transactionTypeOptions.map(eachOption => (
                  <option id={eachOption.optionId} key={eachOption.optionId}>
                    {eachOption.displayText}
                  </option>
                ))}
              </select>

              <button
                className="add-btn"
                type="button"
                onClick={this.addTransaction}
              >
                Add
              </button>
            </form>
          </div>
          <div className="history-container">
            <h1 className="history-heading">History</h1>
            <div>
              <tr className="table-heading-row">
                <th className="title-heading">Title</th>
                <th className="amount-heading">Amount</th>
                <th className="type-heading">Type</th>
                <th>
                  <img
                    className="delete-icon-head"
                    src="https://assets.ccbp.in/frontend/react-js/money-manager/delete.png"
                    alt="delete"
                  />
                </th>
              </tr>
              {historyList.map(eachHistory => (
                <TransactionItem
                  historyDetail={eachHistory}
                  key={eachHistory.id}
                  deleteHistory={this.deleteHistory}
                />
              ))}
            </div>
          </div>
        </div>
      </div>
    )
  }
}

export default MoneyManager
